import { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';

export interface Colors {
    id: number;
    title: string;
    userName: string;
    numViews: number;
    numVotes: number;
    numComments: number;
    numHearts: number;
    rank: number;
    dateCreated: string;
    hex: string;
    rgb: Rgb;
    hsv: Hsv;
    description: string;
    url: string;
    imageUrl: string;
    badgeUrl: string;
    apiUrl: string;
}
export interface Rgb {
    red: number;
    green: number;
    blue: number;
}
export interface Hsv {
    hue: number;
    saturation: number;
    value: number;
}

const URL = 'http://localhost:8080/colors';

function FetchData() {

    const [data, setData] = useState<Colors[]>([]);

    useEffect(() => {

        const fetchData = async () => {

            const res = await fetch(URL);
            const result: Colors[] = await res.json();
            console.log(result);
            setData(result);
        }
        fetchData();
    }, []);

    return (

        <div className="container" style={{ width: "40vw" }}>
            {data.map(color => (
                <div style={{ height: "45vh", marginBottom: "5vh", marginTop: "5vh", width: "40vw", textAlign: "center", boxShadow: "rgba(0, 0, 0, 0.35) 0px 5px 15px" }} className="card" key={color.id}>
                    <div style={{ position:"relative" }}>
                        <img className="card-img-top" src={color.imageUrl} width={100} height={250}></img>
                        <span className="badge " style={{ position:"absolute", color:"black", left:"1vw", top: "1vh"}}>{color.hex}</span>
                    </div>
                    <div className="card-text">
                        <table className="table table-bordered">
                            <thead>
                                <th scope="col">ID</th>
                                <th scope="col">Title</th>
                                <th scope="col">Username</th>
                                <th scope="col">Views</th>
                                <th scope="col">Votes</th>
                                <th scope="col">Comments</th>
                                <th scope="col">Hearts</th>
                                <th scope="col">Rank</th>
                                <th scope="col">Date Created</th>
                            </thead>
                            <tbody>
                                <tr>
                                    <td scope="row">{color.id}</td>
                                    <td>{color.title}</td>
                                    <td>{color.userName}</td>
                                    <td>{color.numViews}</td>
                                    <td>{color.numVotes}</td>
                                    <td>{color.numComments}</td>
                                    <td>{color.numHearts}</td>
                                    <td>{color.rank}</td>
                                    <td>{color.dateCreated}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default FetchData;
